CREATE PROCEDURE CountCities@Count intAsEXEC sp_execute_external_script@language = N'Python',@script = N'OutputDataSet = InputDataSet.sample(n)',@input_data_1 = N'SELECT CityID, CityName FROM Application.Cities',@params = N'@n int',@n = @CountWITH RESULT SETS (([CityID] int, [CityName] nvarchar(50)));-- EXEC BetterRandomNumbers @Count = 20